    private void jBtnAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtnAceptarActionPerformed
        // TODO add your handling code here:
        if (jTCantidad.getText().equals("")) {
            JOptionPane.showMessageDialog(null, "Escriba la cantidad que desea cambiar");
        } else {
            if (jConvertir.getSelectedItem().equals("Celsius") && jConvertirA.getSelectedItem().equals("Faharenheit")) {
                double calculoConvesion = ((Double.parseDouble(jTCantidad.getText()) * 9 / 5))+32;
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °C " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °F" );
            }else if(jConvertir.getSelectedItem().equals("Celsius") && jConvertirA.getSelectedItem().equals("Kelvin")){
                double calculoConvesion = (Double.parseDouble(jTCantidad.getText())+273.15);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °C " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °K" );
            }else if(jConvertir.getSelectedItem().equals("Celsius") && jConvertirA.getSelectedItem().equals("Rankine")){
                double calculoConvesion = (Double.parseDouble(jTCantidad.getText())+273.15)*9/5;
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °C " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °R" );
            }else if(jConvertir.getSelectedItem().equals("Celsius") && jConvertirA.getSelectedItem().equals("Réaumur")){
                double calculoConvesion = (Double.parseDouble(jTCantidad.getText())+273.15)*9/5;
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °C " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °Re" );
            }else if(jConvertir.getSelectedItem().equals("Kelvin") && jConvertirA.getSelectedItem().equals("Celsius")){
                double calculoConvesion = (Double.parseDouble(jTCantidad.getText())-273.15);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °K " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °C" );
            }else if(jConvertir.getSelectedItem().equals("Kelvin") && jConvertirA.getSelectedItem().equals("Faharenheit")){
                double calculoConvesion = ((Double.parseDouble(jTCantidad.getText())-273.15)*9/5)+32;
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °K " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °F" );
            }else if(jConvertir.getSelectedItem().equals("Kelvin") && jConvertirA.getSelectedItem().equals("Rankine")){
                double calculoConvesion = (Double.parseDouble(jTCantidad.getText())*9/5);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °K " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °R" );
            }else if(jConvertir.getSelectedItem().equals("Kelvin") && jConvertirA.getSelectedItem().equals("Réaumur")){
                double calculoConvesion = ((Double.parseDouble(jTCantidad.getText())-273.15)*4/5);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °K " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °Ra" );
            }else if(jConvertir.getSelectedItem().equals("Faharenheit") && jConvertirA.getSelectedItem().equals("Celsius")){
                double calculoConvesion = ((Double.parseDouble(jTCantidad.getText())-32)* 5/ 9);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °F " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °C" );
            }else if(jConvertir.getSelectedItem().equals("Faharenheit") && jConvertirA.getSelectedItem().equals("Kelvin")){
                double calculoConvesion = ((Double.parseDouble(jTCantidad.getText())-32)* 5/ 9)+273.15;
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °F " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °K" );
            }else if(jConvertir.getSelectedItem().equals("Faharenheit") && jConvertirA.getSelectedItem().equals("Rankine")){
                double calculoConvesion = (Double.parseDouble(jTCantidad.getText())+459.67);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °F " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °R" );
            }else if(jConvertir.getSelectedItem().equals("Faharenheit") && jConvertirA.getSelectedItem().equals("Réaumur")){
                double calculoConvesion = ((Double.parseDouble(jTCantidad.getText())-32)* 4/ 9);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °F " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °Ra" );
            }else if(jConvertir.getSelectedItem().equals("Rankine") && jConvertirA.getSelectedItem().equals("Celsius")){
                double calculoConvesion = ((Double.parseDouble(jTCantidad.getText())-491.67)*5/9);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °R " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °C" );
            }else if(jConvertir.getSelectedItem().equals("Rankine") && jConvertirA.getSelectedItem().equals("Faharenheit")){
                double calculoConvesion = (Double.parseDouble(jTCantidad.getText())-459.67);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °R " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °F" );
            }else if(jConvertir.getSelectedItem().equals("Rankine") && jConvertirA.getSelectedItem().equals("Kelvin")){
                double calculoConvesion = (Double.parseDouble(jTCantidad.getText())*5/9);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °R " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °K" );
            }else if(jConvertir.getSelectedItem().equals("Rankine") && jConvertirA.getSelectedItem().equals("Réaumur")){
                double calculoConvesion = ((Double.parseDouble(jTCantidad.getText())-491.67)*4/9);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °R " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °Ra" );
            }else if(jConvertir.getSelectedItem().equals("Réaumur") && jConvertirA.getSelectedItem().equals("Celsius")){
                double calculoConvesion = (Double.parseDouble(jTCantidad.getText())*5/4);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °Ra " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °C" );
            }else if(jConvertir.getSelectedItem().equals("Réaumur") && jConvertirA.getSelectedItem().equals("Faharenheit")){
                double calculoConvesion = ((Double.parseDouble(jTCantidad.getText())*9/4)+32);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °Ra " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °F" );
            }else if(jConvertir.getSelectedItem().equals("Réaumur") && jConvertirA.getSelectedItem().equals("Kelvin")){
                double calculoConvesion = ((Double.parseDouble(jTCantidad.getText())*5/4)+273.15);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °Ra " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °K" );
            }else if(jConvertir.getSelectedItem().equals("Réaumur") && jConvertirA.getSelectedItem().equals("Rankine")){
                double calculoConvesion = ((Double.parseDouble(jTCantidad.getText())*9/4)+491.67);
                JOptionPane.showMessageDialog(null, jTCantidad.getText() + " °Ra " +  " equivale a : " + String.format("%.6f", calculoConvesion) + " °R" );
            }
        }
        jTCantidad.setText("");
        jTCantidad.grabFocus();
    }//GEN-LAST:event_jBtnAceptarActionPerformed

